INSERT INTO `sanpham` (`idSP`, `idLoaiSP`, `idHangSX`, `TenSP`, `GiaSP`, `GiaKhuyenMai`, `MoTaSP`, `ChiTietSP`, `urlHinhSP`, `NgayDang`, `TonKho`, `GhiChu`, `SoLanMua`, `SoLanXem`, `SanPhamMoi`) VALUES
(1, 1, 1, 'iPhone 5s 16GB', '5990000', '5490000', '<ul>\r\n	<li>M&agrave;n H&igrave;nh:&nbsp;4 inch (640 x 1136 pixels )</li>\r\n	<li>Camera:&nbsp;Ch&iacute;nh : 8 MP, Phụ 1.2 MP</li>\r\n	<li>Pin:&nbsp;Li-ion 1560 mAh</li>\r\n	<li>Ram:&nbsp;1GB</li>\r\n	<li>CPU :&nbsp;Apple A7, 2 Nh&acirc;n, Dual-core 1.3Ghz</li>\r\n	<li>HĐH:&nbsp;iOS</li>\r\n</ul>', '<h2>Thiết kế sang trọng, gia c&ocirc;ng tỉ mỉ, t&iacute;ch hợp cảm biến v&acirc;n tay cao cấp hơn, camera cho h&igrave;nh ảnh đẹp v&agrave; s&aacute;ng hơn.</h2>\r\n\r\n<h3><strong>Sang trọng trong thiết kế</strong></h3>\r\n\r\n<p>Thiết kế được giữ nguy&ecirc;n so với phi&ecirc;n bản đầu l&agrave; iPhone 5, c&aacute;c đường cắt kim loại được gia c&ocirc;ng tỉ mỉ, s&aacute;ng b&oacute;ng nh&igrave;n&nbsp;rất đẹp mắt, m&aacute;y c&oacute; 3 m&agrave;u l&agrave; X&aacute;m đậm, V&agrave;ng đồng v&agrave; m&agrave;u Bạc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/60546/iphone-5s-16gb1-1.jpg\" onclick=\"return false;\"><img alt=\"Sang trọng trong thiết kế\" src=\"https://cdn2.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb1-1.jpg\" title=\"Sang trọng trong thiết kế\" /></a></p>\r\n\r\n<p><em>Ba m&agrave;u chủ đạo của iPhone 5S</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/60546/iphone-5s-16gb3-1.jpg\" onclick=\"return false;\"><img alt=\"Sang trọng trong thiết kế\" src=\"https://cdn4.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb3-1.jpg\" title=\"Sang trọng trong thiết kế\" /></a></p>\r\n\r\n<p><em>C&aacute;c cổng kết nối được tập trung ở cạnh dưới</em></p>\r\n\r\n<h3><strong>Cảm biến v&acirc;n tay đầu ti&ecirc;n</strong></h3>\r\n\r\n<p>iPhone 5S ch&iacute;nh l&agrave; chiếc iPhone đầu ti&ecirc;n được t&iacute;ch hợp cảm biến v&acirc;n tay, điểm nhận dạng ch&iacute;nh l&agrave; v&ograve;ng tr&ograve;n &aacute;nh kim v&ograve;ng quanh n&uacute;t Home.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/60546/iphone-5s-16gb88.gif\" onclick=\"return false;\"><img alt=\"Cách đăng ký vân tay và sử dụng rất đơn giản\" src=\"https://cdn1.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb88.gif\" title=\"Cách đăng ký vân tay và sử dụng rất đơn giản\" /></a></p>\r\n\r\n<p><em>C&aacute;ch đăng k&yacute; v&acirc;n tay v&agrave; sử dụng rất đơn giản</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" src=\"https://www.youtube.com/embed/yfu0QaXzyQ0?rel=0\"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Xem th&ecirc;m c&aacute;ch hướng dẫn sử dụng bảo mật bằng v&acirc;n tay</em></p>\r\n\r\n<h3><strong>Camera kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng ch&ecirc;</strong></h3>\r\n\r\n<p>Thao t&aacute;c chụp nhanh ch&oacute;ng v&agrave; nhẹ nh&agrave;ng, ảnh lấy n&eacute;t nhanh v&agrave; m&agrave;u sắc ghi lại tốt với camera 8 MP của iPhone 5S. Xem th&ecirc;m c&aacute;c t&iacute;nh năng chụp ảnh&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/huong-dan-cac-tinh-nang-camera-iphone-5s-658187\" target=\"_blank\" title=\"Hướng dẫn các tính năng camera iPhone 5S\"><strong>tại đ&acirc;y</strong></a>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb4-2.jpg\" /><img src=\"https://cdn.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb5-2.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>C&ugrave;ng độ ph&acirc;n giải nhưng h&igrave;nh ảnh tr&ecirc;n iPhone 5S được cải thiện tốt hơn so với iPhone 5</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/60546/iphone-5s-16gb9a.jpg\" onclick=\"return false;\"><img alt=\"Tĩnh lặng\" src=\"https://cdn3.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb9a.jpg\" title=\"Tĩnh lặng\" /></a></p>\r\n\r\n<p><em>&quot;Tĩnh lặng&quot; (Ảnh:&nbsp;<a href=\"https://www.thegioididong.com/anh-dep/thong-tin-nguoi-choi-1698362\" target=\"_blank\" title=\"Thông tin người dùng\">L&ecirc; Đ&igrave;nh Hiển</a>)</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/60546/iphone-5s-16gb6-2.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp dưới trời nắng (Ảnh: Hoàng Quốc Tuân)\" src=\"https://cdn.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb6-2.jpg\" title=\"Ảnh chụp dưới trời nắng (Ảnh: Hoàng Quốc Tuân)\" /></a></p>\r\n\r\n<p><em>Ảnh chụp dưới trời nắng (Ảnh: Ho&agrave;ng Quốc Tu&acirc;n)</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/60546/iphone-5s-16gb7-1.jpg\" onclick=\"return false;\"><img alt=\"Chỉ với độ phân giải 1.2 MP nhưng những gì iPhone 5S thể hiện được là rất đẹp mắt, ảnh chân thật, không bị ảo (Ảnh: Dương Trịnh Giang Ngân)\" src=\"https://cdn2.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb7-1.jpg\" title=\"Chỉ với độ phân giải 1.2 MP nhưng những gì iPhone 5S thể hiện được là rất đẹp mắt, ảnh chân thật, không bị ảo (Ảnh: Dương Trịnh Giang Ngân)\" /></a></p>\r\n\r\n<p><em>Chỉ với độ ph&acirc;n giải 1.2 MP nhưng những g&igrave; iPhone 5S thể hiện được rất đẹp mắt, ảnh ch&acirc;n thật, kh&ocirc;ng bị ảo (Ảnh: Dương Trịnh Giang Ng&acirc;n)</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/60546/iphone-5s-16gb8-1.jpg\" onclick=\"return false;\"><img alt=\"Ảnh selfie tuoi uxih\" src=\"https://cdn4.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb8-1.jpg\" title=\"Ảnh selfie tuoi uxih\" /></a></p>\r\n\r\n<p><em>M&agrave;u sắc t&aacute;i tạo kh&aacute; tốt (Ảnh:&nbsp;<a href=\"https://www.thegioididong.com/anh-dep/thong-tin-nguoi-choi-1688472\" target=\"_blank\" title=\"Thông tin người dùng\">tuoi uxih</a>)</em></p>\r\n\r\n<h3><strong>Cấu h&igrave;nh mạnh mẽ</strong></h3>\r\n\r\n<p>iPhone 5S sử dụng chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-ve-chip-xu-ly-danh-cho-di-dong-cua-apple-592848#applea7\" target=\"_blank\" title=\"Tìm hiểu chip xử lý Apple A7\">Apple A7</a>&nbsp;2 nh&acirc;n, tốc độ&nbsp;1.3 GHz, việc xảy ra độ trễ tr&ecirc;n m&aacute;y l&agrave; hầu như kh&ocirc;ng c&oacute;, thoải m&aacute;i thao t&aacute;c l&agrave;m việc v&agrave; giải tr&iacute;.&nbsp;Hiện tại, bạn c&oacute; thể n&acirc;ng cấp l&ecirc;n IOS 11 mới nhất như tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-x-64gb\" target=\"_blank\" title=\"iPhone X\" type=\"iPhone X\">iPhone X</a>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" src=\"https://www.youtube.com/embed/sbbi8EVfaXk?rel=0\"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Video thử khả năng nhanh nhạy của m&aacute;y (Nguồn: Youtube)</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" src=\"https://www.youtube.com/embed/bHOP6dgq4Lc?rel=0\"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>C&aacute;c game đồ họa nặng cũng kh&ocirc;ng l&agrave;m kh&oacute; được iPhone 5S</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/60546/iphone-5s-16gb9b.jpg\" onclick=\"return false;\"><img alt=\"LED-backlit IPS LCD\" src=\"https://cdn1.tgdd.vn/Products/Images/42/60546/iphone-5s-16gb9b.jpg\" title=\"LED-backlit IPS LCD\" /></a></p>\r\n\r\n<p><em>iPhone 5S c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh 4 inch, khả năng nh&igrave;n g&oacute;c rộng rất tốt, tuy nhi&ecirc;n m&agrave;n h&igrave;nh nhỏ cũng l&agrave; một nhược điểm m&agrave; người d&ugrave;ng mong muốn được cải thiện</em></p>\r\n\r\n<p>Sang trọng trong thiết kế, mạnh mẽ trong hiệu năng v&agrave; camera chất lượng l&agrave; những g&igrave; m&agrave; iPhone 5S mang lại cho người d&ugrave;ng của m&igrave;nh.</p>\r\n\r\n<p>(Nguồn : thegioididong.com)</p>', 'pTmX_iphone-5s-1.jpg', '2017-12-14', 3110, 'Không', 256, 2694, 0),
(3, 1, 1, 'iPhone 6s Plus 32GB', '14499000', '0', '<ul>\r\n	<li>M&agrave;n H&igrave;nh:&nbsp;5.5 inch, 1080 x 1920 pixels</li>\r\n	<li>Camera:&nbsp;12.0 MP/ 5.0MP</li>\r\n	<li>Pin:&nbsp;lithium-ion battery 2750mAh</li>\r\n	<li>Ram:&nbsp;2 GB</li>\r\n	<li>CPU :&nbsp;Apple A9, 2 Nh&acirc;n, Dual-core 1.8 GHz</li>\r\n	<li>HĐH:&nbsp;iOS 10</li>\r\n</ul>', '<h2>iPhone 6s Plus 32 GB l&agrave;&nbsp;phi&ecirc;n bản&nbsp;n&acirc;ng cấp ho&agrave;n hảo từ iPhone 6 Plus với nhiều t&iacute;nh năng mới hấp dẫn.</h2>\r\n\r\n<h3><strong>Camera được cải tiến</strong></h3>\r\n\r\n<p>iPhone 6s Plus 32 GB được n&acirc;ng cấp độ ph&acirc;n giải camera sau l&ecirc;n 12 MP (thay v&igrave; 8 MP như tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-6-plus-16gb\" target=\"_blank\" title=\"Thông tin điện thoại iPhone 6 Plus 16 GB\">iPhone 6 Plus</a>), camera cho tốc độ lấy n&eacute;t v&agrave; chụp nhanh, thao t&aacute;c chạm để chụp nhẹ nh&agrave;ng. Chất lượng ảnh trong c&aacute;c điều kiện chụp kh&aacute;c nhau tốt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus2-1.jpg\" /><img src=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus1-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>M&agrave;u s&aacute;ng hơn hẳn so với iPhone 6 Plus</em></p>\r\n\r\n<p>Camera trước với độ ph&acirc;n giải 5 MP cho h&igrave;nh ảnh với độ chi tiết r&otilde; n&eacute;t, đặc biệt m&aacute;y c&ograve;n c&oacute; t&iacute;nh năng Retina Flash, sẽ gi&uacute;p m&agrave;n h&igrave;nh s&aacute;ng l&ecirc;n như đ&egrave;n Flash để bức ảnh khi bạn chụp trong trời tối được tốt hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/71770/iphone-6s-plus213.gif\" onclick=\"return false;\"><img alt=\"Để bật tính năng Retina Flash, tại camera trước bạn bật đèn Flash lên\" src=\"https://cdn4.tgdd.vn/Products/Images/42/71770/iphone-6s-plus213.gif\" title=\"Để bật tính năng Retina Flash, tại camera trước bạn bật đèn Flash lên\" /></a></p>\r\n\r\n<p><em>Để bật t&iacute;nh năng Retina Flash, tại camera trước bạn bật đ&egrave;n Flash l&ecirc;n</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus1-2.jpg\" /><img src=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus2-2.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Đ&egrave;n Flash gi&uacute;p ảnh được s&aacute;ng hơn</em></p>\r\n\r\n<h3><strong>Th&iacute;ch th&uacute; hơn với m&agrave;n h&igrave;nh rộng</strong></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh lớn c&ugrave;ng&nbsp;m&agrave;u sắc tươi tắn, độ n&eacute;t cao&nbsp;sẽ mang đến nhiều&nbsp;th&iacute;ch th&uacute; khi lướt web, xem phim hay l&agrave;m việc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/71770/iphone-6s-plus4-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình lớn 5.5 inch thoải mái để làm việc và giải trí\" src=\"https://cdn1.tgdd.vn/Products/Images/42/71770/iphone-6s-plus4-1.jpg\" title=\"Màn hình lớn 5.5 inch thoải mái để làm việc và giải trí\" /></a></p>\r\n\r\n<p><em>M&agrave;n h&igrave;nh lớn 5.5 inch thoải m&aacute;i để l&agrave;m việc v&agrave; giải tr&iacute;</em></p>\r\n\r\n<h3><strong>Cảm ứng 3D Touch độc đ&aacute;o</strong></h3>\r\n\r\n<p>3D Touch l&agrave; t&iacute;nh năng ho&agrave;n to&agrave;n mới tr&ecirc;n iPhone 6s Plus 32 GB, cho ph&eacute;p người d&ugrave;ng xem trước được c&aacute;c t&ugrave;y chọn nhanh dựa v&agrave;o lực nhấn mạnh hay nhẹ m&agrave; kh&ocirc;ng cần phải nhấp v&agrave;o ứng dụng.</p>\r\n\r\n<p>Để sử dụng, bạn chỉ cần nhấn v&agrave;o m&agrave;n h&igrave;nh hoặc ứng dụng 1 lực mạnh đến khi m&aacute;y rung nhẹ l&agrave; c&oacute; thể xem được.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/71770/iphone-6s-plus342.gif\" onclick=\"return false;\"><img alt=\"Chọn nhanh các lựa chọn trên camera của máy\" src=\"https://cdn3.tgdd.vn/Products/Images/42/71770/iphone-6s-plus342.gif\" title=\"Chọn nhanh các lựa chọn trên camera của máy\" /></a></p>\r\n\r\n<p><em>Chọn nhanh c&aacute;c lựa chọn tr&ecirc;n camera của m&aacute;y</em></p>\r\n\r\n<p>Đ&aacute;ng tiếc t&iacute;nh năng 3D Touch n&agrave;y chỉ mới được &aacute;p dụng tr&ecirc;n c&aacute;c&nbsp;<a href=\"https://www.thegioididong.com/game-ung-dung?key=c%E1%BB%A7a+apple\" target=\"_blank\" title=\"Các game và ứng dụng cho hệ điều hành Apple\">ứng dụng của Apple</a>&nbsp;như: danh bạ, camera, mail, m&aacute;y ảnh ...&nbsp;</p>\r\n\r\n<p>Bạn c&oacute; thể t&igrave;m hiểu th&ecirc;m t&iacute;nh năng 3D Touch tại&nbsp;<strong><a href=\"https://www.thegioididong.com/tin-tuc/tong-hop-tat-ca-nhung-tien-ich-3d-touch-dem-den-cho-nguoi-dung-714800\" target=\"_blank\" title=\"Tìm hiểu thêm các chức năng 3D Touch\">đ&acirc;y</a></strong>.</p>\r\n\r\n<h3><strong>Sức mạnh của bộ vi xử l&yacute; A9 mới nhất</strong></h3>\r\n\r\n<p>iPhone 6s Plus 32 GB sử dụng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/chip-xu-ly-apple-a9-tren-iphone-6s-va-6s-plus-733695\" target=\"_blank\" title=\"Tìm hiểu rõ hơn về chip xử lý A9\">vi xử l&yacute; A9</a>&nbsp;tốc độ 1.8 GHz (iPhone 6 Plus chỉ với 1.4 GHz), gi&uacute;p m&aacute;y chạy c&ugrave;ng l&uacute;c nhiều ứng dụng mượt m&agrave;.&nbsp;Hiện tại, bạn c&oacute; thể n&acirc;ng cấp l&ecirc;n IOS 11 mới nhất như tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-x-64gb\" target=\"_blank\" title=\"iPhone X\" type=\"iPhone X\">iPhone X</a>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" src=\"https://www.youtube.com/embed/ZowcYYl-UM0?rel=0\"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Bạn sẽ thực sự cảm nhận được sức mạnh của iPhone 6s Plus 32 GB khi chiến c&aacute;c game c&oacute; đồ họa nặng như&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/modern-combat-5-blackout-game-bom-tan-do-bo-len-ca-556327\" target=\"_blank\" title=\"Thông tin thêm về game Modern Combat\">Modern Combat 5</a>&nbsp;hay&nbsp;Warhammer 40.000</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" src=\"https://www.youtube.com/embed/338nP7G8TfI?rel=0\"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Người trợ l&yacute; ảo rất hữu dụng tr&ecirc;n c&aacute;c d&ograve;ng m&aacute;y iPhone (Nguồn: Youtube)</em></p>\r\n\r\n<p>Vi&ecirc;n pin chỉ c&oacute; dung lượng 2750 mAh kh&aacute; thấp, tuy nhi&ecirc;n bạn vẫn c&oacute; thể an t&acirc;m sử dụng m&aacute;y trong một ng&agrave;y.</p>\r\n\r\n<p>Một chiếc điện thoại vừa thể hiện đẳng cấp của bạn vừa mang lại những n&acirc;ng cấp tốt hơn như camera, hiệu năng hoạt động mạnh mẽ hơn, t&iacute;nh năng 3D Touch độc đ&aacute;o, tất cả sẽ l&agrave; trải nghiệm mới mẻ cho bạn khi chọn mua iPhone 6s Plus 32 GB.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>(Nguồn : thegioididong.com)</p>\r\n\r\n<p>&nbsp;</p>', 'PKbd_tzLN_iphone-6-32GB-2.jpg', '2017-12-12', 365, 'Không', 154, 978, 0),
