--
-- Cấu trúc bảng cho bảng `tin`
--

CREATE TABLE `tin` (
  `idTin` int(11) NOT NULL,
  `idLoaiTin` int(11) NOT NULL,
  `urlHinhTin` varchar(150) NOT NULL,
  `TieuDe` varchar(500) NOT NULL,
  `TomTat` text NOT NULL,
  `NoiDung` longtext NOT NULL,
  `NgayDang` date NOT NULL,
  `SoLanXem` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `tin`
--

INSERT INTO `tin` (`idTin`, `idLoaiTin`, `urlHinhTin`, `TieuDe`, `TomTat`, `NoiDung`, `NgayDang`, `SoLanXem`) VALUES
(1, 1, 'tin_1.jpg', 'Đây là mối đe dọa lớn nhất tới Windows khi Microsoft đang đánh mất mảng di động', '<p>Chỉ trong v&ograve;ng 1 tuần, 2 doanh nghiệp lớn tuy&ecirc;n bố sẽ chuyển sang tập trung sử dụng thiết bị iOS. Nhưng một khi iOS đ&atilde; xuất hiện trong m&ocirc;i trường doanh nghiệp, macOS sẽ l&agrave; hệ lụy tất yếu tiếp theo.</p>', '<h2>Chỉ trong v&ograve;ng 1 tuần, 2 doanh nghiệp lớn tuy&ecirc;n bố sẽ chuyển sang tập trung sử dụng thiết bị iOS. Nhưng một khi iOS đ&atilde; xuất hiện trong m&ocirc;i trường doanh nghiệp, macOS sẽ l&agrave; hệ lụy tất yếu tiếp theo.</h2>\r\n\r\n<p>Khi Apple bắt tay với IBM v&agrave; mới đ&acirc;y l&agrave; General Electrics để ph&aacute;t triển c&aacute;c ứng dụng &ldquo;c&ocirc;ng nghiệp&rdquo; cho nền iOS, một hệ lụy tất yếu kh&aacute;c sẽ theo sau: c&aacute;c đối t&aacute;c của Apple chuyển sang sử dụng m&aacute;y Mac.</p>\r\n\r\n<p>V&agrave; khi đối t&aacute;c của Apple chuyển sang sử dụng m&aacute;y Mac, họ sẽ nhanh ch&oacute;ng nhận ra c&aacute;c lợi &iacute;ch của những chiếc PC gắn m&aacute;c T&aacute;o. Cha đẻ của chiếc Personal Computer đ&atilde; đưa MS-DOS v&agrave; Windows l&ecirc;n đỉnh cao trớ tr&ecirc;u thay lại ch&iacute;nh l&agrave; người ủng hộ mạnh mẽ nhất cho tr&agrave;o lưu &ldquo;Mac-h&oacute;a&rdquo;:&nbsp;<em>&ldquo;Sau 3 hay 4 năm m&aacute;y Mac vẫn c&ograve;n gi&aacute; trị thanh l&yacute;. Mỗi chiếc Mac m&agrave; ch&uacute;ng t&ocirc;i mua đều tiết kiệm tiền cho IBM&rdquo;</em>, một nh&agrave; l&atilde;nh đạo của IBM khẳng định.</p>\r\n\r\n<p>Nh&agrave; l&atilde;nh đạo n&agrave;y cũng cho biết chỉ 5% người d&ugrave;ng Mac cần hỗ trợ kỹ thuật trong khi với PC Windows con số n&agrave;y l&ecirc;n tới 40%. Chia sẻ của IBM ho&agrave;n to&agrave;n tr&ugrave;ng khớp với một g&atilde; khổng lồ kh&aacute;c trong lĩnh vực doanh nghiệp l&agrave; SAP: &ldquo;M&aacute;y Mac l&agrave; yếu tố thiết yếu cho bất cứ một doanh nghiệp hiện đại n&agrave;o&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\r\nIBM, cha đẻ của PC, là một nguồn ảnh hưởng lớn đằng sau xu thế Mac hóa tại các doanh nghiệp lớn.\r\n\" id=\"img_9028a100-b930-11e7-a510-617b959eff96\" src=\"http://genknews.genkcdn.vn/2017/44324j-1508900383570.jpg\" /></p>\r\n\r\n<p>IBM, &quot;cha đẻ của PC&quot;, l&agrave; một nguồn ảnh hưởng lớn đằng sau xu thế &quot;Mac h&oacute;a&quot; tại c&aacute;c doanh nghiệp lớn.</p>\r\n\r\n<p>Đến tuần n&agrave;y, &ldquo;huyền thoại&rdquo; General Electrics đ&atilde; trở th&agrave;nh t&ecirc;n tuổi tiếp theo tham gia v&agrave;o li&ecirc;n minh của Apple với IBM, SAP, Deloitte, Cisco, Accenture trong tham vọng x&acirc;m chiếm lĩnh vực doanh nghiệp. Một lần nữa, ứng dụng iOS vẫn l&agrave; trọng t&acirc;m được ưu ti&ecirc;n, song m&aacute;y Mac cũng được hưởng lợi: GE sẽ sớm triển khai 330.000 m&aacute;y Mac để phục vụ cho tầm nh&igrave;n mới.</p>\r\n\r\n<p>Đến ng&agrave;y h&ocirc;m nay, một trong những h&atilde;ng h&agrave;ng kh&ocirc;ng lớn nhất của Mỹ l&agrave; Delta Airlines đ&atilde; bị r&ograve; rỉ kế hoạch &quot;thay m&aacute;u&quot; to&agrave;n bộ điện thoại Windows Phone bằng iPhone v&agrave; iPad. 300.000 nh&acirc;n vi&ecirc;n của h&atilde;ng h&agrave;ng kh&ocirc;ng n&agrave;y sẽ chuyển sang sử dụng thiết bị di động của T&aacute;o.</p>\r\n\r\n<p>Bước đi của Delta Airline ho&agrave;n to&agrave;n tr&ugrave;ng khớp với quyết định thay thế Windows Phone bằng iPhone của Sở Cảnh s&aacute;t New York (NYPD) v&agrave;o cuối th&aacute;ng 8. V&agrave; khi c&aacute;c tổ chức, doanh nghiệp lớn đặt ch&acirc;n l&ecirc;n nền tảng của T&aacute;o, một kịch bản tất yếu sẽ xảy ra: bộ phận IT của Delta Airlines&nbsp;<strong>chắc chắn sẽ chứng kiến lượng m&aacute;y Mac tăng vọt, bởi macOS l&agrave; m&ocirc;i trường duy nhất c&oacute; thể hỗ trợ ph&aacute;t triển ứng dụng iOS</strong>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Khi các doanh nghiệp phổ cập iOS cho nhân viên, hệ lụy tất yếu là số lượng máy Mac được sử dụng (ít nhất là trong bộ phận IT) chắc chắn sẽ gia tăng.\" id=\"img_907401e0-b930-11e7-a28f-415381e7ee7b\" src=\"http://genknews.genkcdn.vn/thumb_w/660/2017/airdropiphoneipadmachero-1508900383568.jpg\" /></p>\r\n\r\n<p>Khi c&aacute;c doanh nghiệp phổ cập iOS cho nh&acirc;n vi&ecirc;n, hệ lụy tất yếu l&agrave; số lượng m&aacute;y Mac được sử dụng (&iacute;t nhất l&agrave; trong bộ phận IT) chắc chắn sẽ gia tăng.</p>\r\n\r\n<p>V&agrave; IBM, GE cũng như c&aacute;c tập đo&agrave;n cốt l&otilde;i được Apple mời l&agrave;m bạn cũng sẽ đưa ra khuyến c&aacute;o về m&aacute;y Mac tới kh&aacute;ch h&agrave;ng của ch&iacute;nh họ - những doanh nghiệp, tổ chức sử dụng iOS l&agrave;m thiết bị di động chủ yếu như Delta Airlines.</p>\r\n\r\n<p>X&eacute;t cho c&ugrave;ng, c&aacute;c dịch vụ dữ liệu doanh nghiệp vẫn cần giữ được t&iacute;nh đa nền, song việc sử dụng chung một hệ sinh th&aacute;i phần cứng tr&ecirc;n c&aacute;c hệ điều h&agrave;nh gần s&aacute;t nhau, chung ng&ocirc;n ngữ như iOS v&agrave; macOS sẽ gi&uacute;p c&aacute;c doanh nghiệp tiết kiệm được đ&aacute;ng kể c&ocirc;ng sức, tiền bạc v&agrave; thời gian d&agrave;nh cho kh&acirc;u ph&aacute;t triển/triển khai phần mềm.</p>\r\n\r\n<p>Kh&ocirc;ng kh&oacute; để nhận ra xu thế n&agrave;y sẽ g&acirc;y ảnh hưởng đ&aacute;ng kể đến Microsoft. Đầu ti&ecirc;n, Microsoft mất đi doanh thu trực tiếp từ Windows v&agrave; c&aacute;c dịch vụ đi k&egrave;m. Tiếp theo, khi nỗ lực phủ s&oacute;ng dịch vụ đ&aacute;m m&acirc;y qua Android cũng sẽ bị cản trở - Microsoft sẽ buộc phải đẩy mạnh c&aacute;c dịch vụ cung cấp cho người d&ugrave;ng macOS v&agrave; iOS. N&oacute;i c&aacute;ch kh&aacute;c, Microsoft sẽ tạo ra lợi thế cạnh tranh cho đối thủ của Windows.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Microsoft đã không còn kỳ thị các hệ điều hành đối thủ.\" id=\"img_01b0a7a0-b931-11e7-9751-0d5dfb819ce7\" src=\"http://genknews.genkcdn.vn/thumb_w/660/2017/teamshero622v3a-1508900573695.jpg\" /></p>\r\n\r\n<p>Microsoft đ&atilde; kh&ocirc;ng c&ograve;n &quot;kỳ thị&quot; c&aacute;c hệ điều h&agrave;nh đối thủ.</p>\r\n\r\n<p>Một kỷ nguy&ecirc;n cạnh tranh mới sẽ lại bắt đầu: Mac lại được th&ecirc;m 1 cơ hội để đe dọa tới PC. Dĩ nhi&ecirc;n, l&agrave; g&atilde; khổng lồ đứng thứ hai tr&ecirc;n thị trường đ&aacute;m m&acirc;y, l&agrave; &ocirc;ng chủ của những c&ocirc;ng nghệ kh&ocirc;ng thể thay thế như Office, Exchange, Visio v&agrave; cũng l&agrave; một thế lực về Big Data c&ugrave;ng AI, Microsoft sẽ kh&ocirc;ng rơi v&agrave;o cảnh khốn đốn v&igrave; vấn đề n&agrave;y. C&aacute;c dịch vụ đ&aacute;m m&acirc;y của Microsoft vẫn sẽ sống tốt tr&ecirc;n macOS v&agrave; iOS. Trong thời đại Satya Nadella, Microsoft đ&atilde; kh&ocirc;ng c&ograve;n gay gắt với c&aacute;c đối thủ như Mac v&agrave; Linux như trước kia nữa.</p>\r\n\r\n<p>Thế nhưng, sự thật vẫn chỉ c&oacute; 1: Mac l&agrave; đối thủ đ&aacute;ng kể duy nhất của những chiếc Windows tr&ecirc;n thị trường người d&ugrave;ng cuối cho doanh nghiệp. Đi k&egrave;m với sự phổ biến của c&aacute;c thiết bị iOS, xu thế &ldquo;Mac h&oacute;a&rdquo; của c&aacute;c doanh nghiệp lớn l&agrave; kh&ocirc;ng thể tr&aacute;nh khỏi. Khi trượt ch&acirc;n tr&ecirc;n di động, Microsoft c&oacute; lẽ đ&atilde; kh&ocirc;ng thể ngờ rằng hậu họa của một thất bại nhất thời lại k&eacute;o d&agrave;i đến thế.</p>', '2017-11-24', 116),
(2, 2, 'tat_thong_bao_messeger.jpg', 'Hướng dẫn tắt thông báo EverWing trên Messenger để không bị làm phiền nữa', '<p>Nếu bạn cảm thấy cực k&igrave; ức chế v&igrave; li&ecirc;n tục &ldquo;nhảy&rdquo; th&ocirc;ng b&aacute;o về game Everwing th&igrave; h&atilde;y đọc hướng dẫn n&agrave;y để tắt đi ngay b&acirc;y giờ.</p>', '<h4><strong>Nếu bạn cảm thấy cực k&igrave; ức chế v&igrave; li&ecirc;n tục &ldquo;nhảy&rdquo; th&ocirc;ng b&aacute;o về game Everwing th&igrave; h&atilde;y đọc hướng dẫn n&agrave;y để tắt đi ngay b&acirc;y giờ.</strong></h4>\r\n\r\n<h3><strong>Everwing &ndash; tr&ograve; chơi đang g&acirc;y sốt tại Việt Nam</strong></h3>\r\n\r\n<p>Ngay sau khi nền tảng Instant Game tr&ecirc;n Facebook Messenger c&oacute; mặt tại Việt Nam; c&aacute;c Facebooker đ&atilde; &ldquo;ph&aacute;t sốt&rdquo; với tựa game Everwing, hay c&ograve;n gọi với c&aacute;i t&ecirc;n th&acirc;n thương l&agrave; &ldquo;Ti&ecirc;n bắn quỷ&rdquo;.</p>\r\n\r\n<p><img alt=\"tắt-thông-báo-everwing-2 Hướng dẫn tắt thông báo EverWing trên Messenger để không bị làm phiền nữa\" src=\"https://hoanghamobile.com/tin-tuc/wp-content/uploads/2017/10/t%E1%BA%AFt-th%C3%B4ng-b%C3%A1o-everwing-2.jpg\" style=\"height:400px; width:640px\" title=\"\" /></p>\r\n\r\n<p><em>Tựa game EverWing chơi trực tiếp tr&ecirc;n ứng dụng Messenger đang g&acirc;y sốt</em></p>\r\n\r\n<p>Tựa game n&agrave;y c&oacute; hơi hướng giống game Bắn Ruồi ng&agrave;y xưa với mức độ g&acirc;y nghiện cực cao. Tuy nhi&ecirc;n, c&aacute;ch hiển thị th&ocirc;ng b&aacute;o của n&oacute; c&ograve;n g&acirc;y ức chế hơn c&aacute;c tựa game truyền thống tr&ecirc;n Facebook.</p>\r\n\r\n<p>Mỗi khi c&oacute; bạn b&egrave; đạt điểm cao hơn hay đang chơi, Everwing sẽ gửi th&ocirc;ng b&aacute;o về thiết bị th&ocirc;ng qua một cửa sổ chat ri&ecirc;ng. Thậm ch&iacute;, th&ocirc;ng b&aacute;o n&agrave;y cũng xuất hiện trong c&aacute;c cửa sổ chat c&aacute; nh&acirc;n một c&aacute;ch cực k&igrave; &ldquo;v&ocirc; duy&ecirc;n&rdquo;. Ch&iacute;nh v&igrave; vậy, c&oacute; kh&ocirc;ng &iacute;t người tỏ ra kh&oacute; chịu khi điện thoại chứ nhảy noti li&ecirc;n tục.</p>\r\n\r\n<h3><strong>L&agrave;m sao để tắt th&ocirc;ng b&aacute;o Everwing?</strong></h3>\r\n\r\n<p>Cũng may, v&igrave; c&aacute;c th&ocirc;ng b&aacute;o hiển thị trong cửa sổ chat n&ecirc;n ch&uacute;ng ta c&oacute; thể ẩn ch&uacute;ng đi. Cụ thể, với người d&ugrave;ng iOS v&agrave; Android, h&atilde;y truy cập v&agrave;o ứng dụng Messenger; t&igrave;m tới cửa sổ chat với Everwing; chọn&nbsp;<strong><em>Quản l&yacute; Tin nhắn</em></strong>&nbsp;(Manage/Manage Messagges) v&agrave; gạt c&ocirc;ng tắc&nbsp;<em><strong>Th&ocirc;ng b&aacute;o</strong></em>&nbsp;sang tr&aacute;i.</p>\r\n\r\n<p><img alt=\"tắt-thông-báo-everwing-4 Hướng dẫn tắt thông báo EverWing trên Messenger để không bị làm phiền nữa\" src=\"https://hoanghamobile.com/tin-tuc/wp-content/uploads/2017/10/t%E1%BA%AFt-th%C3%B4ng-b%C3%A1o-everwing-4.jpg\" style=\"height:391px; width:660px\" title=\"\" /></p>\r\n\r\n<p><em>C&aacute;ch tắt th&ocirc;ng b&aacute;o EverWing cho Android v&agrave; iOS</em></p>\r\n\r\n<p>Bạn cũng c&oacute; thể chọn&nbsp;<em><strong>Chặn th&ocirc;ng b&aacute;o</strong></em>&nbsp;từ t&agrave;i khoản của Everwing. Tuy nhi&ecirc;n, nhiều người đ&atilde; x&aacute;c nhận l&agrave; c&aacute;c th&ocirc;ng b&aacute;o game vẫn bị gửi đến b&igrave;nh thường.</p>\r\n\r\n<p>Nếu d&ugrave;ng m&aacute;y t&iacute;nh, bạn h&atilde;y truy cập v&agrave;o page của Everwing&nbsp;<a href=\"https://www.facebook.com/EverWing-411339792577492/\" rel=\"noopener\" target=\"_blank\">tại đ&acirc;y</a>. Sau đ&oacute;, bấm chọn&nbsp;<em><strong>Nhắn tin</strong></em>; bấm tiếp n&uacute;t&nbsp;<em><strong>C&agrave;i đặt</strong></em>&nbsp;h&igrave;nh b&aacute;nh răng v&agrave; chọn&nbsp;<em><strong>Quản l&yacute; Tin nhắn</strong></em>. Tại đ&acirc;y, h&atilde;y chọn&nbsp;<em><strong>Tắt tất cả th&ocirc;ng b&aacute;o</strong>&nbsp;</em>l&agrave; được.</p>\r\n\r\n<p>Ngo&agrave;i ra, với những người &ldquo;bạn tốt&rdquo;, cố t&igrave;nh gửi tin nhắn game hoặc tạo group spam chỉ để chơi game, h&atilde;y y&ecirc;u cầu họ ngừng lại hoặc tự tay tắt th&ocirc;ng b&aacute;o đi. Ch&uacute;c bạn c&oacute; những khoảng thời gian y&ecirc;n b&igrave;nh hơn khi sử dụng Facebook.</p>', '2017-11-24', 999),
(3, 1, 'qualcomm.jpg', 'Qualcomm, ZTE và China Mobile thử nghiệm thành công mạng 5G đầu tiên trên thế giới', '<p>(Techz.vn) V&agrave;o h&ocirc;m thứ s&aacute;u vừa qua (17/11), Qualcomm, ZTE v&agrave; nh&agrave; mạng China Mobile th&ocirc;ng b&aacute;o đ&atilde; thử nghiệm th&agrave;nh c&ocirc;ng mạng 5G đầu ti&ecirc;n tr&ecirc;n thế giới.</p>', '<p>(Techz.vn) V&agrave;o h&ocirc;m thứ s&aacute;u vừa qua (17/11), Qualcomm, ZTE v&agrave; nh&agrave; mạng China Mobile th&ocirc;ng b&aacute;o đ&atilde; thử nghiệm th&agrave;nh c&ocirc;ng mạng 5G đầu ti&ecirc;n tr&ecirc;n thế giới.</p>\r\n\r\n<p><img alt=\"Qualcomm, ZTE và China Mobile thử nghiệm thành công mạng 5G đầu tiên trên thế giới\" src=\"http://cache.media.techz.vn/upload/2017/11/18/image-1511022313-qualcomm-5g-movil.jpg\" style=\"height:317px; width:660px\" title=\"Qualcomm, ZTE và China Mobile thử nghiệm thành công mạng 5G đầu tiên trên thế giới\" /></p>\r\n\r\n<p>Hệ thống được thử nghiệm cho ph&eacute;p truyền dữ liệu ở tốc độ gigabit/gi&acirc;y (&gt;1 Gbps) với độ trễ dưới 4 ms. Cải thiện rất nhiều so với con số 20 ms tr&ecirc;n c&aacute;c mạng LTE hiện tại.</p>\r\n\r\n<p>Hệ thống mới n&agrave;y dựa tr&ecirc;n ti&ecirc;u chuẩn 3GPP (Generation Partnership Project). Nh&oacute;m n&agrave;y bao gồm c&aacute;c c&ocirc;ng ty viễn th&ocirc;ng thiết lập c&aacute;c ti&ecirc;u chuẩn cho mạng v&agrave; thiết bị 3G, 4G v&agrave; b&acirc;y giờ l&agrave; 5G.</p>\r\n\r\n<p>Ti&ecirc;u chuẩn 3GPP sẽ được sử dụng trong ng&agrave;nh c&ocirc;ng nghiệp khi c&aacute;c mạng 5G được x&acirc;y dựng. Nh&oacute;m nghi&ecirc;n cứu hiện đ&atilde; đ&aacute;p ứng được c&aacute;c th&ocirc;ng số 3GPP Release 15 5G New Radio , mặc d&ugrave; phi&ecirc;n bản 15 sẽ phải đợi đến th&aacute;ng 9/2018.</p>\r\n\r\n<p>Về việc thử nghiệm, c&aacute;c thiết bị được sử dụng gồm trạm thương mại 5G ZTE, mẫu thử nghiệm 5G tần số 6 GHz từ Qualcomm, c&aacute;c dữ liệu được truyền qua băng tần 3.5 GHz với băng th&ocirc;ng 100 MHz.</p>', '2017-11-24', 123),
(4, 2, 'emoji.png', 'Mang tính năng Animoji “siêu đáng yêu” trên iPhone X lên iPhone đời cũ', '<p>(Techz.vn) Bạn c&oacute; thể tải ứng dụng SuperMoji để trải nghiệm t&iacute;nh năng th&uacute; vị tương tự Animoji tr&ecirc;n iPhone X.</p>', '<p><strong>Bạn muốn sử dụng Animoji tuy nhi&ecirc;n kh&ocirc;ng c&oacute; điều kiện mua iPhone X? Nhờ &ldquo;SUPERMOJI&rdquo; sẽ gi&uacute;p bạn trải nghiệm t&iacute;nh năng n&agrave;y tr&ecirc;n bất cữ chiếc iPhone n&agrave;o.</strong></p>\r\n\r\n<p><img alt=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 1)\" id=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 1)\" longdesc=\"https://fptshop.com.vn/tin-tuc/thu-thuat/Tr%E1%BA%A3i%20nghi%E1%BB%87m%20Animoji%20tr%C3%AAn%20iPhone%20c%C5%A9%20m%E1%BB%99t%20c%C3%A1ch%20th%C3%BA%20v%E1%BB%8B%20(%E1%BA%A2nh%201)\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/PSPVu/Th%C3%A1ng%2011/Th%E1%BB%A7%20thu%E1%BA%ADt/mang-animoji-len-cac-iPhone-cu-1.jpg\" style=\"height:479px; width:660px\" title=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 1)\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Animoji l&agrave; một trong những t&iacute;nh năng đặc biệt nhất được t&iacute;ch hợp tr&ecirc;n iPhone X của Apple. Người d&ugrave;ng c&oacute; thể thoải m&aacute;i tạo ra những đoạn ghi &acirc;m với h&igrave;nh ảnh gương mặt được ghi lại bằng cảm biến TrueDepth. T&iacute;nh năng Animoji l&agrave; độc quyền tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-x-64gb\" target=\"_blank\">iPhone X</a>&nbsp;nhưng bạn ho&agrave;n to&agrave;n c&oacute; thể trải nghiệm n&oacute; tr&ecirc;n c&aacute;c phi&ecirc;n bản iPhone cũ hơn bằng ứng dụng SUPERMOJI.</p>\r\n\r\n<p>Ứng dụng n&agrave;y hoạt động ở iOS 11 tr&ecirc;n tất cả c&aacute;c phi&ecirc;n bản iOS cũ như iPhone cũ. Nhưng h&atilde;y nhớ rằng ứng dụng n&agrave;y kh&ocirc;ng thể n&agrave;o hoạt động ho&agrave;n hảo như iPhone X, m&agrave; chỉ gi&uacute;p bạn trải nghiệm phần n&agrave;o animoji m&agrave; th&ocirc;i.</p>\r\n\r\n<p>Bạn cũng đừng lo, ứng dụng n&agrave;y hoạt động tương đối tốt khi ph&acirc;n t&iacute;ch v&agrave; ghi lại biểu cảm gương mặt tương đối tốt. Ch&uacute;ng ta c&ograve;n c&oacute; thể th&ecirc;m hiệu ứng v&agrave;o bất k&igrave; nh&acirc;n vật emoji n&agrave;o bạn th&iacute;ch.</p>\r\n\r\n<p>Để sử dụng bạn l&agrave;m theo c&aacute;c bước sau:</p>\r\n\r\n<p><strong>Bước 1:</strong>&nbsp;Đầu ti&ecirc;n, h&atilde;y tải v&agrave; c&agrave;i đặt ứng dụng tr&ecirc;n App Store ở link dưới, ứng dụng n&agrave;y ho&agrave;n to&agrave;n miễn ph&iacute;:&nbsp;<a href=\"https://itunes.apple.com/us/app/supermoji-the-emoji-app/id1300364926?mt=8\" rel=\"nofollow\" target=\"_blank\" title=\"SUPERMOJI\" type=\"SUPERMOJI\">SUPERMOJI</a></p>\r\n\r\n<p><strong>Bước 2:&nbsp;</strong>Mở ứng dụng iMessage l&ecirc;n v&agrave; v&agrave;o một cuộc tr&ograve; chuyện bất k&igrave;</p>\r\n\r\n<p><strong>Bước 3:</strong>&nbsp;Chọn v&agrave;o biểu tượng ứng dụng v&agrave; chọn ứng dụng SUPERMOJI.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 2)\" id=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 2)\" longdesc=\"https://fptshop.com.vn/tin-tuc/thu-thuat/Tr%E1%BA%A3i%20nghi%E1%BB%87m%20Animoji%20tr%C3%AAn%20iPhone%20c%C5%A9%20m%E1%BB%99t%20c%C3%A1ch%20th%C3%BA%20v%E1%BB%8B%20(%E1%BA%A2nh%202)\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/PSPVu/Th%C3%A1ng%2011/Th%E1%BB%A7%20thu%E1%BA%ADt/mang-animoji-len-cac-iPhone-cu-2.jpg\" style=\"height:660px; width:660px\" title=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 2)\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 4:</strong>&nbsp;Ứng dụng sẽ ngay lập tức hiện một nh&acirc;n vật emoji, bạn h&atilde;y nh&igrave;n v&agrave;o camera của iPhone v&agrave; ấn v&ograve;ng tr&ograve;n ph&iacute;a dưới để tiến h&agrave;nh quay lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 3)\" id=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 3)\" longdesc=\"https://fptshop.com.vn/tin-tuc/thu-thuat/Tr%E1%BA%A3i%20nghi%E1%BB%87m%20Animoji%20tr%C3%AAn%20iPhone%20c%C5%A9%20m%E1%BB%99t%20c%C3%A1ch%20th%C3%BA%20v%E1%BB%8B%20(%E1%BA%A2nh%203)\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/PSPVu/Th%C3%A1ng%2011/Th%E1%BB%A7%20thu%E1%BA%ADt/mang-animoji-len-cac-iPhone-cu-3.jpg\" style=\"height:660px; width:660px\" title=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 3)\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 5:</strong>&nbsp;Sau khi quay xong, h&atilde;y nhấn v&agrave;o biểu tượng gửi để gửi Animoji đ&oacute; đến bạn b&egrave;.</p>\r\n\r\n<p>Để chọn nh&acirc;n vật kh&aacute;c c&aacute;c bạn chỉ cần ấn v&agrave;o mũi t&ecirc;n như h&igrave;nh b&ecirc;n dưới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 4)\" id=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 4)\" longdesc=\"https://fptshop.com.vn/tin-tuc/thu-thuat/Tr%E1%BA%A3i%20nghi%E1%BB%87m%20Animoji%20tr%C3%AAn%20iPhone%20c%C5%A9%20m%E1%BB%99t%20c%C3%A1ch%20th%C3%BA%20v%E1%BB%8B%20(%E1%BA%A2nh%204)\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/PSPVu/Th%C3%A1ng%2011/Th%E1%BB%A7%20thu%E1%BA%ADt/mang-animoji-len-cac-iPhone-cu-4.jpg\" style=\"height:660px; width:660px\" title=\"Trải nghiệm Animoji trên iPhone cũ một cách thú vị (Ảnh 4)\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ đơn giản vậy th&ocirc;i l&agrave; bạn đ&atilde; c&oacute; thể trải nghiệm v&agrave; gửi cho bạn b&egrave; của m&igrave;nh Animoji được quay tr&ecirc;n c&aacute;c điện thoại iPhone&nbsp;đời cũ. Ch&uacute;c bạn th&agrave;nh c&ocirc;ng v&agrave; h&atilde;y chia sẻ cho bạn b&egrave; của m&igrave;nh nh&eacute;!</p>', '2017-11-24', 550),
(5, 1, 'tin_iphoneX.jpg', 'iPhone X chính hãng sẽ bán ra vào 8/12, 30 triệu đồng cho bản 64GB', '<p>(Techz.vn) iPhone X ch&iacute;nh h&atilde;ng sẽ c&oacute; mặt sớm tại thị trường Việt Nam hơn dự kiến ban đầu.</p>', '<p>(Techz.vn) iPhone X ch&iacute;nh h&atilde;ng sẽ c&oacute; mặt sớm tại thị trường Việt Nam hơn dự kiến ban đầu.</p>\r\n\r\n<p>Theo th&ocirc;ng tin vừa được tiết lộ, FPT Shop v&agrave; F.Studio l&agrave; một trong số những đơn vị đầu ti&ecirc;n tại thị trường Việt Nam ph&acirc;n phối ch&iacute;nh thức iPhone X. Dự kiến v&agrave;o đầu th&aacute;ng sau, mẫu m&aacute;y cao cấp nhất của Apple sẽ ch&iacute;nh thức l&ecirc;n kệ.</p>\r\n\r\n<p>Cụ thể hơn, iPhone X sẽ được b&aacute;n ra tại FPT Shop v&agrave;o ng&agrave;y 8/12. V&agrave; mức gi&aacute; đưa ra cũng ho&agrave;n to&agrave;n c&oacute; thể dự đo&aacute;n được,&nbsp;<em>iPhone X 64GB</em>&nbsp;c&oacute; gi&aacute;<strong>&nbsp;29,99 triệu đồng</strong>&nbsp;v&agrave;&nbsp;<em>iPhone X 256GB</em>&nbsp;c&oacute; gi&aacute;&nbsp;<strong>34,79 triệu đồng.&nbsp;</strong></p>\r\n\r\n<p><img alt=\"iPhone X chính hãng sẽ bán ra vào 8/12, 30 triệu đồng cho bản 64GB\" src=\"http://cache.media.techz.vn/upload/2017/11/16/image-1510791889-071010-thietke2.jpg\" style=\"height:375px; width:660px\" title=\"iPhone X chính hãng sẽ bán ra vào 8/12, 30 triệu đồng cho bản 64GB\" /></p>\r\n\r\n<p>Tất nhi&ecirc;n, gi&aacute; b&aacute;n n&agrave;y sẽ &aacute;p dụng cho cả 2 phi&ecirc;n bản m&agrave;u Silver v&agrave; Space Grey. iPhone X ch&iacute;nh h&atilde;ng được ph&acirc;n phối tại Việt Nam sẽ c&oacute; m&atilde; VN/A. C&aacute;c phụ kiện cũng tương đồng so với những d&ograve;ng sản phẩm trước đ&oacute;, bao gồm vỏ hộp với c&aacute;c th&ocirc;ng tin bằng tiếng Việt, s&aacute;ch hướng dẫn sử dụng, d&acirc;y lightning v&agrave; củ sạc 2 ch&acirc;n.</p>\r\n\r\n<p>Rất nhiều khả năng, sau khi FPT b&aacute;n ra iPhone X. C&aacute;c đơn vị c&ograve;n lại được Apple uỷ quyền như Thế Giới Di Động, Viettel Store cũng sẽ đồng loạt ph&acirc;n phối mẫu m&aacute;y n&agrave;y.&nbsp;</p>\r\n\r\n<p>Nếu bạn đang c&oacute; &yacute; định t&igrave;m mua iPhone X x&aacute;ch tay v&agrave;o thời điểm hiện tại, th&igrave; h&atilde;y cố gắng đợi sang th&aacute;ng 12 để c&oacute; một mức gi&aacute; tốt hơn.&nbsp;</p>', '2017-11-24', 2323),
(6, 1, 'animoji_2.jpg', 'Animoji - Tính năng độc nhất của iPhone X có thể xuất hiện trên iPhone đời cũ', '<p>(Techz.vn) T&iacute;nh năng Animoji c&oacute; thể xuất hiện tr&ecirc;n c&aacute;c mẫu iPhone đời cũ trong tương lai.</p>', '<p>(Techz.vn) T&iacute;nh năng Animoji c&oacute; thể xuất hiện tr&ecirc;n c&aacute;c mẫu iPhone đời cũ trong tương lai.</p>\r\n\r\n<p>Thực chất Animoji hoạt động kh&ocirc;ng li&ecirc;n quan g&igrave; đến hệ thống camera TrueDepth của iPhone X. M&agrave; n&oacute; chỉ l&agrave; một chức năng phụ thuộc ch&iacute;nh v&agrave;o camera trước.&nbsp;</p>\r\n\r\n<p><img alt=\"Animoji - Tính năng độc nhất của iPhone X có thể xuất hiện trên iPhone đời cũ\" src=\"http://cache.media.techz.vn/upload/2017/11/15/image-1510758191-animoji-1.jpg\" style=\"height:372px; width:660px\" title=\"Animoji - Tính năng độc nhất của iPhone X có thể xuất hiện trên iPhone đời cũ\" /></p>\r\n\r\n<p><em>T&iacute;nh năng độc đ&aacute;o Animoji biểu cảm tương tự như khu&ocirc;n mặt của bạn</em></p>\r\n\r\n<p>Điều n&agrave;y đ&atilde; được chứng minh trong đoạn video review của Marques Brownlee dưới đ&acirc;y. Từ ph&uacute;t 11:40, mặc d&ugrave; chiếc iPhone X đ&atilde; được che đi cảm biến FaceID th&igrave; Animoji vẫn hoạt động được. C&oacute; nghĩa rằng chức năng n&agrave;y sẽ được thực thi nhờ v&agrave;o camera trước, điều m&agrave; hầu hết c&aacute;c mẫu iPhone đời cũ đều được trang bị.&nbsp;</p>\r\n\r\n<p>Ph&aacute;t hiện n&agrave;y cho thấy rằng, việc Apple mang t&iacute;nh năng Animoji l&ecirc;n c&aacute;c mẫu iPhone đời cũ l&agrave; điều ho&agrave;n to&agrave;n c&oacute; thể được. Vấn đề ở đ&acirc;y l&agrave; Apple c&oacute; muốn hỗ trợ kh&ocirc;ng, hay chỉ &ldquo;độc quyền&rdquo; cho sản phẩm cao cấp nhất hiện nay l&agrave; iPhone X.<img alt=\"Animoji - Tính năng độc nhất của iPhone X có thể xuất hiện trên iPhone đời cũ\" src=\"http://cache.media.techz.vn/upload/2017/11/15/image-1510758551-Screen%20Shot%202017-11-15%20at%2010.01.47%20PM.png\" style=\"height:391px; width:660px\" title=\"Animoji - Tính năng độc nhất của iPhone X có thể xuất hiện trên iPhone đời cũ\" /><em>Animoji hoạt động dựa tr&ecirc;n camera trước thay v&igrave; hệ thống TrueDept</em></p>\r\n\r\n<p>Như vậy, cảm biến TrueDept tr&ecirc;n iPhone X nhiều khả năng chỉ đ&oacute;ng vai tr&ograve; nhỏ trong chức năng Animoj, đ&oacute; ch&iacute;nh l&agrave; gi&uacute;p c&aacute;c biểu cảm c&aacute;c biểu tượng được thực hiện r&otilde; r&agrave;ng, chi tiết v&agrave; mượt m&agrave; hơn. Đồng thời tiết kiệm năng lượng hơn cho chiếc iPhone X khi thực hiện t&iacute;nh năng n&agrave;y trong thời gian d&agrave;i.&nbsp;</p>\r\n\r\n<p><object height=\"473\" width=\"630\"></object></p>', '2017-11-24', 425),
(7, 1, 'tin_onePlus5.jpg', 'OnePlus tự tin khẳng định OnePlus 5T là smartphone mạnh mẽ hàng đầu hiện nay', '<p>(Techz.vn) OnePlus 5T vừa mới được OnePlus ra mắt thực sự l&agrave; một con &ldquo;qu&aacute;i th&uacute;&rdquo;.</p>', '<p>(Techz.vn) OnePlus 5T vừa mới được OnePlus ra mắt thực sự l&agrave; một con &ldquo;qu&aacute;i th&uacute;&rdquo;.</p>\r\n\r\n<p>OnePlus 5T l&agrave; phi&ecirc;n bản n&acirc;ng cấp của d&ograve;ng 5 với kiểu d&aacute;ng thiết kế mới. M&aacute;y tuy kh&ocirc;ng trang bị những c&ocirc;ng nghệ hiện đại h&agrave;ng đầu hiện nay, nhưng 5T g&acirc;y được ấn tượng mạnh mẽ bởi th&ocirc;ng số cấu h&igrave;nh si&ecirc;u khủng. Kết hợp c&ugrave;ng giao diện tối ưu Oxygen (Android 7.1), &iacute;t tuỳ biến, kh&ocirc;ng kh&oacute; để OnePlus 5T vượt mặt c&aacute;c mẫu flagship h&agrave;ng đầu hiện nay.</p>\r\n\r\n<p><img alt=\"OnePlus tự tin khẳng định OnePlus 5T là smartphone mạnh mẽ hàng đầu hiện nay\" src=\"http://cache.media.techz.vn/upload/2017/11/18/image-1510997867-akrales_171115_2123_0085.jpg\" style=\"height:440px; width:660px\" title=\"OnePlus tự tin khẳng định OnePlus 5T là smartphone mạnh mẽ hàng đầu hiện nay\" /></p>\r\n\r\n<p>Về cấu h&igrave;nh, OnePlus 5T trang bị cấu h&igrave;nh tương tự như chiếc 5, bao gồm VXL Snapdragon 835, 6GB RAM &amp; 8GB RAM. Nhưng kh&ocirc;ng hiểu v&igrave; một l&iacute; do n&agrave;o đ&oacute;, OnePlus tự tin khẳng định 5T l&agrave; mẫu di động mạnh mẽ nhất hiện nay.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cụ thể, OnePlus tự tin tuy&ecirc;n bố rằng OnePlus 5 mang trong m&igrave;nh tốc độ tải ứng dụng v&agrave; game nhanh nhất hiện nay. Khi bạn được dịp trải nghiệm th&igrave; sẽ kh&ocirc;ng c&ograve;n muốn thử một thiết bị di động n&agrave;o kh&aacute;c.&nbsp;</p>\r\n\r\n<p>C&ocirc;ng bố n&agrave;y cũng đồng nghĩa với việc OnePlus 5T ch&iacute;nh thức ngh&ecirc;nh chiến với tất cả c&aacute;c d&ograve;ng m&aacute;y flagship hiện nay như iPhone X, Galaxy S8/S8+, Note 8. Nhưng đ&oacute; l&agrave; lời quảng c&aacute;o của nh&agrave; sản xuất, để c&oacute; kết quả ch&iacute;nh x&aacute;c nhất, bạn n&ecirc;n chờ cho đến khi OnePlus 5T ch&iacute;nh thức đến tay người d&ugrave;ng.</p>', '2017-11-24', 123),
(8, 1, 'ios11_bancapnhat.jpg', 'iOS 11 là bản cập nhật tồi tệ nhất từ trước đến nay?', '<p>(Techz.vn) Chủ sở hữu chiếc iPhone 6 đ&atilde; than trời rằng n&acirc;ng cấp l&ecirc;n iOS 11 l&agrave; một quyết định cực kỳ sai lầm.</p>', '<p>Chủ sở hữu chiếc iPhone 6 đ&atilde; than trời rằng n&acirc;ng cấp l&ecirc;n iOS 11 l&agrave; một quyết định cực kỳ sai lầm.</p>\r\n\r\n<p>Một người d&ugrave;ng trung th&agrave;nh của Apple c&oacute; t&ecirc;n l&agrave; Maciej Lipiec đang tỏ ra thất vọng trước quyết định n&acirc;ng cấp l&ecirc;n iOS 11.&nbsp;</p>\r\n\r\n<p>Maciej chia sẻ rằng, chiếc iPhone 6 của anh tuy đ&atilde; cũ nhưng hoạt động rất ổn định, nhưng ch&iacute;nh v&igrave; quyết định cập nhật l&ecirc;n nền tảng iOS 11 đ&atilde; khiến cho anh c&oacute; những t&igrave;nh huống &ldquo;dở kh&oacute;c dở cười&rdquo;.</p>\r\n\r\n<p>Maciej những tưởng rằng nền tảng mới sẽ gi&uacute;p cho chiếc iPhone 6 của anh đ&oacute;n nhận nhiều t&iacute;nh năng mới mẻ hơn, hiệu năng cải thiện so với phi&ecirc;n bản cũ kỹ m&agrave; anh đang d&ugrave;ng. Tuy nhi&ecirc;n, iOS 11 lại l&agrave; &ldquo;con dao&rdquo; giết chết niềm tin với Apple trong mắt của Maciej.</p>\r\n\r\n<p><img alt=\"iOS 11 là bản cập nhật tồi tệ nhất từ trước đến nay?\" src=\"http://cache.media.techz.vn/upload/2017/11/18/image-1510978901-apple-ios-11-review-header.jpg\" style=\"height:440px; width:660px\" title=\"iOS 11 là bản cập nhật tồi tệ nhất từ trước đến nay?\" /></p>\r\n\r\n<p>Cụ thể hơn, anh lu&ocirc;n phải mang theo ổ cắm điện v&igrave; thời lượng pin iOS 11 tr&ecirc;n iPhone 6 sụt cực kỳ nhanh. Thậm ch&iacute; cả hiệu năng của m&aacute;y cũng bị cắt giảm một c&aacute;ch nghi&ecirc;m trọng. Chưa hết, sau khi n&acirc;ng cấp l&ecirc;n nền tảng iOS 11 anh kh&ocirc;ng thể quay về phi&ecirc;n bản cũ l&agrave; iOS 10 v&agrave; iOS 11, bởi Apple đ&atilde; &ldquo;chặn&rdquo; downgrade (kho&aacute; sign) với cả 2 nền tảng n&agrave;y.</p>\r\n\r\n<p>Ngay cả khi n&acirc;ng cấp l&ecirc;n iOS 11, niềm hy vọng nhỏ nhoi duy nhất của anh l&uacute;c n&agrave;y l&agrave; c&aacute;c những trải nghiệm tốt hơn từ c&aacute;c t&iacute;nh năng mới, nhưng nền tảng mới kh&ocirc;ng c&oacute; bất kỳ t&iacute;nh năng n&agrave;o thực sự hữu &iacute;ch với anh. Những g&igrave; c&ograve;n s&oacute;t lại duy nhất Maciej với nền tảng iOS 11 đ&oacute; ch&iacute;nh l&agrave; nỗi bực tức v&agrave; sự thất vọng đối với những g&igrave; Apple đ&atilde; hỗ trợ cho người d&ugrave;ng cũ.</p>\r\n\r\n<p>Maciej cũng đ&atilde; từng c&oacute; &yacute; định n&acirc;ng cấp l&ecirc;n c&aacute;c mẫu iPhone mới trong năm nay, nhưng kh&ocirc;ng c&oacute; nghĩa l&agrave; bị Apple &ldquo;&eacute;p buộc&rdquo; như t&igrave;nh thế hiện tại với chiếc iPhone 6 chạy iOS 11.</p>\r\n\r\n<p>Theo như Maciej đ&atilde; chia sẻ, anh l&agrave; một người t&iacute;n đồ của T&aacute;o Khuyết. Maciej đ&atilde; sử dụng một chiếc MacBook Air từ năm 2011, n&oacute; vẫn hoạt động ổn định với c&aacute;c t&iacute;nh năng cơ bản. Hay anh đ&atilde; sở hữu cả chuột hay b&agrave;n ph&iacute;m, v&agrave; rất nhiều sản phẩm m&agrave; Apple tạo ra. Tất cả những thứ ấy đ&atilde; mang lại cho anh cảm gi&aacute;c h&agrave;i l&ograve;ng, y&ecirc;n t&acirc;m khi sử dụng.</p>\r\n\r\n<p>Nếu bạn l&agrave; một người d&ugrave;ng đ&atilde; n&acirc;ng cấp l&ecirc;n iOS 11, h&atilde;y chia sẻ cho TechZ những cảm nhận của bạn về nền tảng n&agrave;y?&nbsp;&nbsp;</p>', '2017-11-24', 4312),
(9, 1, 'ios11_capnhattocdosac.jpg', 'iOS 11.2 sẽ cải thiện tốc độ sạc cho iPhone X, iPhone 8/ 8 Plus', '<p>(Techz.vn) Những thay đổi tr&ecirc;n bản cập nhật iOS 11.2 sắp tới được kỳ vọng sẽ cải thiện tốc độ sạc kh&ocirc;ng d&acirc;y tr&ecirc;n bộ ba iPhone mới của Apple.</p>', '<p><strong>Những thay đổi tr&ecirc;n bản cập nhật iOS 11.2 sắp tới được kỳ vọng sẽ cải thiện tốc độ sạc kh&ocirc;ng d&acirc;y tr&ecirc;n bộ ba iPhone mới của Apple.</strong></p>\r\n\r\n<p>Một trong những thay đổi lớn nhất tr&ecirc;n d&ograve;ng iPhone 2017 l&agrave; hỗ trợ sạc kh&ocirc;ng d&acirc;y. Đ&acirc;y l&agrave; t&iacute;nh năng đ&atilde; xuất hiện tr&ecirc;n smartphone Android từ c&aacute;ch đ&acirc;y v&agrave;i năm.</p>\r\n\r\n<p>Đ&aacute;ng tiếc, Apple lại hạn chế tốc độ sạc của bộ ba iPhone mới, khi bộ sạc 5W đi k&egrave;m v&agrave; chuẩn sạc kh&ocirc;ng d&acirc;y Qi kh&ocirc;ng c&oacute; sự kh&aacute;c biệt lớn.</p>\r\n\r\n<p><img alt=\"iOS 11.2 sẽ cải thiện tốc độ sạc cho iPhone X, iPhone 8/ 8 Plus\" src=\"http://cache.media.techz.vn/upload/2017/11/15/image-1510719315-222.jpg\" title=\"iOS 11.2 sẽ cải thiện tốc độ sạc cho iPhone X, iPhone 8/ 8 Plus\" /></p>\r\n\r\n<p>Về l&yacute; thuyết, phụ kiện đế sạc kh&ocirc;ng d&acirc;y chuẩn Qi n&agrave;y hỗ trợ 7.5W. Nhưng thực tế, tốc độ sạc kh&ocirc;ng d&acirc;y chỉ ngang bằng với cục sạc Power Adapter 5W b&aacute;n k&egrave;m iPhone 8. N&oacute;i c&aacute;ch kh&aacute;c, Apple kh&ocirc;ng r&otilde; v&igrave; l&yacute; do g&igrave; đ&atilde; &ldquo;gh&igrave;m&rdquo; lại tốc độ sạc kh&ocirc;ng d&acirc;y.</p>\r\n\r\n<p>Nhưng kể từ phi&ecirc;n bản iOS 11.2 sắp tới, Apple sẽ n&acirc;ng tốc độ sạc kh&ocirc;ng d&acirc;y l&ecirc;n 7.5W. Theo tiết lộ của trang&nbsp;<em>Macrumors</em>, tốc độ sạc kh&ocirc;ng d&acirc;y của bộ ba iPhone mới sẽ tăng l&ecirc;n đ&aacute;ng kể nhờ c&ocirc;ng suất 7.5W. D&ugrave; vậy mức c&ocirc;ng suất n&agrave;y vẫn thấp hơn ph&acirc;n nửa so với tốc độ sạc kh&ocirc;ng d&acirc;y tối đa 15W.</p>\r\n\r\n<p><img alt=\"iOS 11.2 sẽ cải thiện tốc độ sạc cho iPhone X, iPhone 8/ 8 Plus\" src=\"http://cache.media.techz.vn/upload/2017/11/15/image-1510719314-111.jpg\" title=\"iOS 11.2 sẽ cải thiện tốc độ sạc cho iPhone X, iPhone 8/ 8 Plus\" /></p>\r\n\r\n<p>Thử nghiệm ban đầu với iPhone X cho thấy dung lượng pin tăng th&ecirc;m 6% trong suốt 30 ph&uacute;t sạc với bộ sạc kh&ocirc;ng d&acirc;y 7.5W.</p>\r\n\r\n<p>Apple đang b&aacute;n lẻ một số đế sạc kh&ocirc;ng d&acirc;y 7.5W từ b&ecirc;n thứ ba. V&agrave; người d&ugrave;ng chắc chắn sẽ phải c&agrave;i iOS 11.2 để tận dụng được t&iacute;nh năng n&agrave;y.</p>\r\n\r\n<p>Hiện tại chỉ c&oacute; bộ ba iPhone 8, 8 Plus v&agrave; iPhone X c&oacute; thể sử dụng sạc kh&ocirc;ng d&acirc;y. Thiết kế mặt lưng k&iacute;nh g&oacute;p phần gi&uacute;p Apple thực hiện h&oacute;a th&agrave;nh c&ocirc;ng t&iacute;nh năng vốn từ l&acirc;u đ&atilde; được iFan mong đợi. H&atilde;ng dự kiến sẽ sớm đưa c&ocirc;ng nghệ n&agrave;y l&ecirc;n c&aacute;c mẫu iPhone trong năm 2018 v&agrave; nhiều năm tới. Gần đ&acirc;y nhất, Apple cũng đạt được một bằng s&aacute;ng chế cho ph&eacute;p sạc kh&ocirc;ng d&acirc;y hoạt động trong kh&ocirc;ng gian rộng lớn hơn.</p>\r\n\r\n<p>Hiện tại, iOS 11.2 mới chỉ l&agrave; phi&ecirc;n bản beta v&agrave; sẽ sớm c&oacute; bản ch&iacute;nh thức. Tuy vậy chưa r&otilde; khi n&agrave;o Apple sẽ ph&aacute;t h&agrave;nh phi&ecirc;n bản iOS 11.2 cho người d&ugrave;ng.</p>\r\n\r\n<p>Cũng trong phi&ecirc;n bản n&agrave;y, nhiều người d&ugrave;ng chờ đợi Apple sẽ sửa c&aacute;c ph&aacute;t sinh như &acirc;m thanh lạ ph&aacute;t ra từ loa trước, đơ cảm ứng khi trời lạnh, lỗi GPS&hellip; tr&ecirc;n iPhone X v&agrave; iPhone 8.</p>\r\n\r\n<p><em>Theo: Vnreview.vn&nbsp;</em></p>', '2017-11-24', 2345),
(10, 1, 'tin_vivo.jpg', 'Vivo V7 trình làng: Màn hình FullView 5.7 inch, camera selfie 24MP, RAM 4GB, giá 6.8 triệu đồng', '<p>(Techz.vn) Vivo đ&atilde; vừa ra mắt chiếc smartphone V7 với viền m&agrave;n h&igrave;nh si&ecirc;u mỏng. Trước đ&oacute; v&agrave;o th&aacute;ng 9, h&atilde;ng cũng đ&atilde; tr&igrave;nh l&agrave;ng chiếc phablet V7 Plus.</p>', '<p>(Techz.vn) Vivo đ&atilde; vừa ra mắt chiếc smartphone V7 với viền m&agrave;n h&igrave;nh si&ecirc;u mỏng. Trước đ&oacute; v&agrave;o th&aacute;ng 9, h&atilde;ng cũng đ&atilde; tr&igrave;nh l&agrave;ng chiếc phablet V7 Plus.</p>\r\n\r\n<p><img alt=\"Vivo V7 trình làng: Màn hình FullView 5.7 inch, camera selfie 24MP, RAM 4GB, giá 6.8 triệu đồng\" src=\"http://cache.media.techz.vn/upload/2017/11/16/image-1510845290-Vivo-V7-Plus.jpg\" style=\"height:344px; width:660px\" title=\"Vivo V7 trình làng: Màn hình FullView 5.7 inch, camera selfie 24MP, RAM 4GB, giá 6.8 triệu đồng\" /></p>\r\n\r\n<p>Vivo V7 trang bị m&agrave;n h&igrave;nh FullView k&iacute;ch thước 5.7 inch độ ph&acirc;n giải HD+ tỷ lệ 18:9. B&ecirc;n trong l&agrave; vi xử l&yacute; Snapdragon 450, dung lượng RAM 4 GB.</p>\r\n\r\n<p>Camera mặt sau độ ph&acirc;n giải 16 MP với khẩu độ f/2.0, trong khi camera trước l&ecirc;n đến 24 MP với c&ocirc;ng nghệ Face Beauty 7.0 gi&uacute;p l&agrave;m đẹp tự nhi&ecirc;n, r&otilde; n&eacute;t ngay cả trong điều kiện thiếu s&aacute;ng.&nbsp;</p>\r\n\r\n<p>Smartphone mới từ nh&agrave; sản xuất Trung Quốc sẽ khởi chạy hệ điều h&agrave;nh Android 7.1 Nougat khi xuất xưởng.</p>\r\n\r\n<p>Vivo sẽ b&aacute;n ra V7 với mức gi&aacute; 300 USD (khoảng 6.8 triệu đồng). H&atilde;ng vẫn chưa cho biết c&aacute;c thị trường m&agrave; V7 sẽ đặt ch&acirc;n đến.</p>', '2017-11-24', 1953),
(11, 1, 'toP2_tin_surface.png', 'Microsoft bật mí bí quyết giúp Surface Book 2 trở thành máy tính bảng có màn hình tốt nhất thế giới hiện tại', 'Với những công sức và nỗ lực Microsoft dồn v&agrave;o chiếc m&aacute;y t&iacute;nh bảng Surface, kh&ocirc;ng qu&aacute; ngạc nhi&ecirc;n khi &ocirc;ng lớn n&agrave;y hết sức tự h&agrave;o với sản phẩm.', '<h4>Với những c&ocirc;ng sức v&agrave; nỗ lực m&agrave; Microsoft dồn v&agrave;o chiếc m&aacute;y t&iacute;nh bảng Surface, kh&ocirc;ng qu&aacute; ngạc nhi&ecirc;n khi &ocirc;ng lớn n&agrave;y hết sức tự h&agrave;o với sản phẩm của m&igrave;nh.</h4>\r\n\r\n<p>Trong buổi giới thiệu chiếc Surface Book 2 với giới b&aacute;o ch&iacute; tr&ecirc;n to&agrave;n thế giới hồi giữa th&aacute;ng 10 tại th&agrave;nh phố New York, Panos Panay d&agrave;nh rất nhiều thời gian để giới thiệu về sự cao cấp của chiếc m&agrave;n h&igrave;nh 2-trong-1 của chiếc Surface.</p>\r\n\r\n<p>V&agrave; sự thật l&agrave; c&ocirc;ng sức v&agrave; t&acirc;m huyết được Microsoft dồn v&agrave;o chiếc Surface Book 2 đ&atilde; biến đ&acirc;y trở th&agrave;nh một sản phẩm rất tuyệt vời, với m&agrave;n h&igrave;nh được thiết kế gần như &quot;chuẩn đến từng Pixel&quot;.</p>\r\n\r\n<p><em>&quot;Khi thiết kế chiếc m&agrave;n h&igrave;nh n&agrave;y, ch&uacute;ng t&ocirc;i đ&atilde; n&oacute;i r&otilde; những y&ecirc;u cầu của m&igrave;nh, như cần một tấm m&agrave;n LCD &acirc;m bản, với số lượng pixel cố định.&quot;&nbsp;</em>- Nh&agrave; thiết kế phần cứng Steve Bathiche tại Microsoft chia sẻ.&nbsp;<em>&quot;Trước đ&acirc;y chưa từng c&oacute; ai l&agrave;m m&agrave;n h&igrave;nh kiểu n&agrave;y, với những y&ecirc;u cầu cụ thể như thế n&agrave;y cả. Với lại, khi nh&igrave;n v&agrave;o những con số của m&agrave;n h&igrave;nh, ch&uacute;ng t&ocirc;i cố gắng để th&ocirc;ng số trở n&ecirc;n đẹp nhất c&oacute; thể, v&agrave; như c&aacute;c bạn c&oacute; thể thấy, đ&acirc;y l&agrave; một chiếc m&agrave;n h&igrave;nh 15 inch, với tỉ lệ 3:2. Điều n&agrave;y cũng đồng nghĩa bề rộng của m&agrave;n h&igrave;nh bằng đ&uacute;ng 12,5 inch, v&agrave; số đẹp như vậy kể cũng kh&aacute; th&uacute; vị.&quot;</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" id=\"img_0148bc20-d0ed-11e7-9619-976b27c23e56\" src=\"http://genknews.genkcdn.vn/thumb_w/660/2017/image-1511510196230.png\" /></p>\r\n\r\n<p><em>&quot;Tất cả những chiếc m&agrave;n h&igrave;nh đều được t&ugrave;y biến theo cấu h&igrave;nh của thiết bị. Microsoft đ&atilde; đổ rất nhiều c&ocirc;ng sức v&agrave;o qu&aacute; tr&igrave;nh thiết kế, đặc biệt l&agrave; v&agrave;o chiếc m&agrave;n h&igrave;nh, từ những t&iacute;ch hợp về mặt cơ học cho đến thiết kế m&agrave;n h&igrave;nh, từ bố cục pixel cho đến loại tinh thể lỏng được sử dụng. Thậm ch&iacute;, ch&uacute;ng t&ocirc;i c&ograve;n can thiệp cả v&agrave;o cấu tr&uacute;c của transistor nếu như cần thiết.&quot;&nbsp;</em>- Steve Bathiche n&oacute;i tiếp.</p>\r\n\r\n<p>Vậy th&igrave; vai tr&ograve; của c&aacute;c đối t&aacute;c sản xuất l&agrave; g&igrave;, nếu như Microsoft đ&atilde; tự m&igrave;nh l&agrave;m hầu như tất cả mọi thứ như thế?</p>\r\n\r\n<p><em>&quot;Đương nhi&ecirc;n đối t&aacute;c của ch&uacute;ng t&ocirc;i cũng l&agrave; c&aacute;c chuy&ecirc;n gia trong lĩnh vực n&agrave;y. Tuy nhi&ecirc;n, ch&uacute;ng t&ocirc;i nhận thấy rằng khi sản xuất một chiếc m&agrave;n h&igrave;nh, c&oacute; rất rất nhiều biến số cần phải lưu t&acirc;m đến, v&agrave; bạn sẽ phải c&acirc;n nhắc đ&aacute;nh đổi giữa hiệu năng v&agrave; mức độ ti&ecirc;u thụ điện, giữa chi ph&iacute; v&agrave; lợi nhuận, v&agrave; những điều n&agrave;y đối t&aacute;c sẽ kh&ocirc;ng thể quyết định thay cho bạn được.&quot;&nbsp;</em>- Bathiche thừa nhận.</p>\r\n\r\n<p><em>&quot;Vậy n&ecirc;n quyết định của ch&uacute;ng t&ocirc;i l&agrave; cử một đội l&agrave;m việc c&ugrave;ng với c&aacute;c đối t&aacute;c, theo d&otilde;i họ s&iacute;t sao từng ch&uacute;t một, đến khi sản phẩm được ho&agrave;n thiện. Sẽ c&oacute; l&uacute;c bọn họ hỏi rằng: &#39;Chiếc m&agrave;n h&igrave;nh n&agrave;y c&oacute; thể sử dụng cho c&aacute;c sản phẩm kh&aacute;c được kh&ocirc;ng&#39;, v&agrave; sẽ c&oacute; những l&uacute;c ch&uacute;ng t&ocirc;i buộc phải trả lời l&agrave; &#39;Kh&ocirc;ng&#39;. Đ&oacute; ch&iacute;nh l&agrave; v&iacute; dụ cho việc tại sao đ&acirc;y l&agrave; chiếc m&agrave;n h&igrave;nh của ch&uacute;ng t&ocirc;i, bởi c&aacute;c đối t&aacute;c sản xuất nhiều l&uacute;c sẽ kh&ocirc;ng thể &aacute;p dụng c&ocirc;ng nghệ n&agrave;y cho sản phẩm của h&atilde;ng kh&aacute;c được.&quot;</em></p>\r\n\r\n<p>V&agrave; ch&iacute;nh c&ocirc;ng sức m&agrave; Microsoft dồn v&agrave;o sản phẩm của m&igrave;nh, c&ugrave;ng với nỗ lực từ c&aacute;c bộ phận ph&aacute;p l&yacute;, đ&atilde; biến những thiết kế sử dụng trong Surface Book 2 trở th&agrave;nh t&agrave;i sản tr&iacute; tuệ của c&ocirc;ng ty.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" id=\"img_e3bfb320-d0ec-11e7-990e-a95a06fbc9a1\" src=\"http://genknews.genkcdn.vn/2017/image-1511510146569.png\" /></p>\r\n\r\n<p><strong>CHIP RI&Ecirc;NG CHO M&Agrave;N H&Igrave;NH</strong></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, đội ngũ tại Redmond, Washington c&ograve;n bỏ c&ocirc;ng sức nghi&ecirc;n cứu thiết kế chip ri&ecirc;ng cho m&agrave;n h&igrave;nh của d&ograve;ng sản phẩm Surface. Loại chip n&agrave;y mang t&ecirc;n TCON, được sử dụng để điều chỉnh thời gian.</p>\r\n\r\n<p>Kh&ocirc;ng, Microsoft vẫn chưa c&oacute; &yacute; định sẽ sớm cạnh tranh với Intel đ&acirc;u. Đ&acirc;y chỉ l&agrave; một con chip xử l&yacute; c&oacute; khả năng điều chỉnh chức năng của từng điểm ảnh trong m&agrave;n h&igrave;nh Surface m&agrave; th&ocirc;i.</p>\r\n\r\n<p><em>&quot;Ch&iacute;nh con chip TCON n&agrave;y l&agrave; thứ điều khiển m&agrave;n h&igrave;nh. Con chip n&agrave;y l&agrave; l&yacute; do tại sao ch&uacute;ng t&ocirc;i lại tự sản xuất m&agrave;n h&igrave;nh của ri&ecirc;ng m&igrave;nh, v&igrave; kh&ocirc;ng ai kh&aacute;c c&oacute; thể l&agrave;m m&agrave;n h&igrave;nh tương th&iacute;ch với hệ thống n&agrave;y tốt hơn ch&uacute;ng t&ocirc;i được. Con chip c&oacute; khả năng đọc một bảng m&agrave;u 3D để hỗ trợ khả năng hiển thị m&agrave;u sắc tối đa, cũng như c&oacute; thể đổi chế độ m&agrave;u chỉ trong chớp mắt.&quot;</em></p>\r\n\r\n<p>Một thứ nữa cũng chỉ c&oacute; ri&ecirc;ng m&agrave;n h&igrave;nh của Surface mới c&oacute;, ch&iacute;nh l&agrave; mạch t&iacute;ch hợp ứng dụng cụ thể ASIC (Application-Specific Integrated Circuit), do ch&iacute;nh tay Microsoft thiết kế.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" id=\"img_17931070-d0ed-11e7-9619-976b27c23e56\" src=\"http://genknews.genkcdn.vn/thumb_w/660/2017/image-1511510233033.png\" /></p>\r\n\r\n<p><em>&quot;Mạch ASIC được thiết kế để phục vụ c&aacute;c t&iacute;nh năng cảm ứng v&agrave; hiển thị, được kết nối trực tiếp đến với chip TCON. Mạch ASIC n&agrave;y c&oacute; 2 đường t&iacute;n hiệu ri&ecirc;ng biệt, một kết nối đến hệ điều h&agrave;nh v&agrave; một kết nối đến m&agrave;n h&igrave;nh. Vậy n&ecirc;n khi b&uacute;t chạm v&agrave;o m&agrave;n h&igrave;nh, t&iacute;n hiệu sẽ được m&agrave;n h&igrave;nh trực tiếp xử l&yacute; v&agrave; hiển thị m&agrave; kh&ocirc;ng cần phải th&ocirc;ng qua hệ điều h&agrave;nh. Đ&acirc;y ch&iacute;nh l&agrave; l&yacute; do tại sao t&iacute;nh năng của b&uacute;t c&oacute; độ trễ chỉ vỏn vẹn 22 miligi&acirc;y, ngắn nhất tr&ecirc;n thế giới ở thời điểm hiện tại.&quot;</em></p>\r\n\r\n<p>Ch&iacute;nh v&igrave; l&yacute; do n&agrave;y, Microsoft c&oacute; thể tự tin khẳng định rằng vẽ hay viết tr&ecirc;n một sản phẩm Surface bằng b&uacute;t Surface chẳng kh&aacute;c g&igrave; cầm b&uacute;t vẽ tr&ecirc;n giấy cả. Tất cả l&agrave; nhờ v&agrave;o hệ thống m&agrave;n h&igrave;nh được Microsoft đầu tư thiết kế ri&ecirc;ng.</p>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh thiết kế v&agrave; sản xuất d&ograve;ng sản phẩm Surface từ năm 2012, Microsoft cũng đ&atilde; đi đến một kết luận giống như Apple từ rất nhiều năm trở về trước: đ&oacute; l&agrave; nếu bạn muốn sản phẩm của m&igrave;nh như &yacute;, bạn phải c&oacute; khả năng tự m&igrave;nh sản xuất mọi th&agrave;nh phần của n&oacute;.</p>\r\n\r\n<p><em>&quot;C&oacute; một điều hết sức quan trọng như thế n&agrave;y: M&agrave;n h&igrave;nh của bạn c&agrave;ng kh&oacute; sản xuất v&agrave; y&ecirc;u cầu c&agrave;ng cao, th&igrave; bạn c&agrave;ng phải nh&uacute;ng tay nhiều hơn v&agrave;o trong c&ocirc;ng đoạn sản xuất. Bạn kh&ocirc;ng thể n&oacute;i với người kh&aacute;c rằng &#39;T&ocirc;i muốn c&oacute; một c&aacute;i m&agrave;n h&igrave;nh c&oacute; những chức năng như thế n&agrave;y, v&agrave; bằng một c&aacute;ch kỳ diệu n&agrave;o đ&oacute; bạn c&oacute; được sản phẩm ho&agrave;n thiện tr&ecirc;n tay. Kh&ocirc;ng, điều đ&oacute; sẽ kh&ocirc;ng bao giờ xảy ra đ&acirc;u.&quot;&nbsp;</em>- Bathiche kết luận.</p>', '2017-11-24', 211),
(12, 1, '3hXx_6.png', 'GGGGGGGG', '<p>g</p>', '<p>g</p>', '2017-12-15', 0);

